interface IconsProps {
  type: string;
  size: number;
}

function Icons({ type, size }: IconsProps) {
  const icons: Record<
    string,
    React.ComponentType<React.SVGProps<SVGSVGElement>>
  > = {
    folder: FolderIcon,
    "folder-plus": FolderPlusIcon,
    folderPlus: FolderPlusIcon,
    folder_plus: FolderPlusIcon,
    upload: ArrowUpTrayIcon,
    arrow: ArrowRightIcon,
    cog: Cog6ToothIcon,
    cog6tooth: Cog6ToothIcon,
    extend: ExtendIcon,
    move: MoveIcon,
    rotate: RotateIcon,
    scale: ScaleIcon,
    selection: SelectionIcon,
    subtract: SubtractIcon,
  };

  const Icon = icons[type];

  // Logic: Pass the size prop into the dynamic Icon component.
  // The svg within the component uses {...props} to accept width and height.
  return Icon ? <Icon width={size} height={size} /> : null;
}

export default Icons;
